var link=document.querySelector(".message-link"),popup=document.querySelector(".modal-message"),overlay=document.querySelector(".overlay"),form=popup.querySelector(".message-form"),email=popup.querySelector("#message-email-id"),message=popup.querySelector("#message-question-id"),close=popup.querySelector(".modal-close");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),overlay.classList.add("overlay-show")}),form.addEventListener("submit",function(e){email.value&&message.value||(e.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),overlay.classList.remove("overlay-show"),popup.classList.remove("modal-error")}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-show")&&(e.preventDefault(),popup.classList.remove("modal-show"),overlay.classList.remove("overlay-show"),popup.classList.remove("modal-error"))});
